function solution(genres, plays) {
    let best = {}
    let answer = []
    const len = genres.length;
    genres.map((val) =&#62; {
        best[val] = []
    })
    for(let i = 0; i &#60; len; i++){
        best[genres[i]].push([i, plays[i]])
    }
    let sortSum = []
    for(let k in best){
        sortSum.push({ name : k , sum : best[k].reduce((a,c) =&#62; a + c[1], 0)})
        best[k].sort((a,b) =&#62; {
            if(b[1] !== a[1]){ 
                return b[1] - a[1] 
            }
            else{
                return a[0] - b[0]
            }
        })
    }
    sortSum.sort((a,b) =&#62; b.sum - a.sum).map((val) =&#62; {
        answer.push(best[val.name][0][0], best[val.name][1][0])
    })
    return answer
}

// 노래의 장르를 나타내는 문자열 배열 genres
// 노래별 재생 횟수를 나타내는 정수 배열 plays
function solution(genres, plays) {

    // 1) 우선 장르와 plays가 합친 자료 구조를 만들어야 한다.
    let genresPlaysMap = {}
    genres.forEach((genre, i) =&#62; {
        genresPlaysMap[genre] = genresPlaysMap[genre] || {genre, plays: []}
        genresPlaysMap[genre].plays.push({i, play: plays[i]})
    })

    // 2) 추가로 필요한 데이터를 찾아보자.
    // 속한 노래가 많이 재생된 장르를 먼저 수록합니다. =&#62; 장르별 재생횟수
    // 장르 내에서 많이 재생된 노래를 먼저 수록합니다. =&#62; 장르별 횟수정렬
    // 장르 내에서 재생 횟수가 같은 노래 중에서는 고유 번호가 낮은 노래를 먼저 수록합니다. =&#62; 같으면 ID순

    // 정렬과 조건에 맞는 값들을 찾아서 추가한다.
    const result = Object.values(genresPlaysMap).map(g =&#62; {
        // 장르별 재생횟수
        const total = g.plays.reduce((a, b) =&#62; a + b.play, 0)

        // 장르 별로 가장 많이 재생된 노래를 최대 두 개까지          
        const top2 = g.plays.slice()
            .sort((a, b) =&#62; b.play - a.play || a.i - b.i)
            .slice(0, 2)
            .map(p =&#62; p.i)

        return {...g, total, top2}
    })
    .sort((a, b) =&#62; b.total - a.total)

    // 베스트 앨범에 들어갈 노래의 고유 번호 순서
    // 답변에서 원하는 형태의 포맷으로 제출한다.
    .map(g =&#62; g.top2)
    .flat()

    return result    
}

