```
type MaybeValue&#60;T&#62; = { exists: true; value: T } | { exists: false };

export class Maybe&#60;T&#62; {
  readonly value: MaybeValue&#60;T&#62;;

  /* Constructors */

  private constructor(value: MaybeValue&#60;T&#62;) {
    this.value = value;
  }
  public static Some&#60;T&#62;(value: T) {
    return new Maybe({ exists: true, value });
  }
  public static None: Maybe&#60;never&#62; = new Maybe({ exists: false });

  /* Methods */

  map&#60;U&#62;(f: (t: T) =&#62; U): Maybe&#60;U&#62; {
    return this.value.exists ? Maybe.Some(f(this.value.value)) : Maybe.None;
  }

  apply&#60;U&#62;(
    mu: Maybe&#60;U&#62;
  ): Maybe&#60;T extends (u: U) =&#62; infer V ? Maybe&#60;V&#62; : never&#62; {
    if (this.value.exists) {
      const f = this.value.value;
      return mu.map(f);
    }
    return Maybe.None;
  }
}
```

